namespace PayrollSystem.Models;public class ComissionWageWorker{        public string FullName { get; set; }        public string Gender { get; set; }        public int Salary { get; set; }        public int Percentage { get; set; }        public void Work(int goodsSold)        {            _goodsSoldSum += goodsSold % MaxPrice;        }        public int CalculateWage()        {            var wage = 0;            var addition = (int)((float)_goodsSoldSum * (float)Percentage / 100);            if (addition != 0)                wage = Salary + addition;            _goodsSoldSum = 0;            return wage;        }        private static int _goodsSoldSum;        private const int MaxPrice = 15000;}