namespace PayrollSystem.Models;public class HourlyWageWorker{    public string FullName { get; set; }    public string Gender { get; set; }    public int NormalSalary { get; set; }    public int OvertimeSalary { get; set; }    public int StandartOfWorkingHours { get; set; }    public void Work(int hours)    {        _hoursWorked += hours % 24;        _workedDays++;    }    public int CalculateWage()    {        var normalHoursWorked = (_hoursWorked < StandartOfWorkingHours * _workedDays) ? _hoursWorked : (StandartOfWorkingHours * _workedDays);        var overtimeHoursWorked = _hoursWorked - normalHoursWorked;        _hoursWorked = 0;        _workedDays = 0;        return normalHoursWorked * NormalSalary + overtimeHoursWorked * NormalSalary;    }    private int _hoursWorked;    private int _workedDays;}