using System;using PayrollSystem.ViewModels;using ReactiveUI;namespace PayrollSystem.ViewModels;public class FireWorkerViewModel : ViewModelBase{    private AddHourlyWageWorkerViewModel _hourlyWage;    private AddComissionWageWorkerViewModel _comissionWage;    private string _workerName;    public FireWorkerViewModel(AddHourlyWageWorkerViewModel addHourlyWageWorkerViewModel, AddComissionWageWorkerViewModel addComissionWageWorkerViewModel)    {        _hourlyWage = addHourlyWageWorkerViewModel;        _comissionWage = addComissionWageWorkerViewModel;    }    public void FireWorker()    {        _comissionWage.FireWorker(_workerName);        _hourlyWage.FireWorker(_workerName);    }        public string WorkerName    {        set        {            if (value != null && !string.Equals(value, ""))            {                this.RaiseAndSetIfChanged(ref _workerName, value);            }        }        get        {            if (_workerName != null)                return _workerName;            throw new NullReferenceException();        }    }}