using ReactiveUI;namespace PayrollSystem.ViewModels{    public class MainWindowViewModel : ViewModelBase, IScreen    {        public string Greeting => "Welcome to Avalonia!";                public RoutingState Router { get; }        public AddHourlyWageWorkerViewModel AddHourlyWageWorkerViewModel { get; }        public AddComissionWageWorkerViewModel AddComissionWageWorkerViewModel { get; }        public FireWorkerViewModel FireWorkerViewModel { get; }        public SimulateWorkViewModel SimulateWorkViewModel { get; }        public MainWindowViewModel()        {            AddHourlyWageWorkerViewModel = new AddHourlyWageWorkerViewModel();            AddComissionWageWorkerViewModel = new AddComissionWageWorkerViewModel();            FireWorkerViewModel = new FireWorkerViewModel(AddHourlyWageWorkerViewModel, AddComissionWageWorkerViewModel);            SimulateWorkViewModel = new SimulateWorkViewModel(AddHourlyWageWorkerViewModel, AddComissionWageWorkerViewModel);        }    }}